  async function* generate(n) {
    for (let i = 0; i < n; i++) {
      let x = await getSomething(i);
      yield x;
    }
  }
  for (let x of generate(10)) { // ITERATE_NON_ITERABLE alarm because async generator functions return async iterable objects.
    doSomething(await x);
  }
}

