import Vue from 'vue';
import App from 'App.vue';
import Comp from 'Comp.vue';

Vue.component('Comp', Comp); // VUE_DEPRECATED_FEATURE alarm because 'Vue.component()' is deprecated.

new Vue({ // VUE_DEPRECATED_FEATURE alarm because 'new Vue()' is deprecated.
  el: '#app', // VUE_DEPRECATED_FEATURE alarm because the 'el' option is deprecated.
  render(createElement) { // VUE_DEPRECATED_FEATURE alarm because the 'createElement' parameter is deprecated.
    return createElement(App);
  },
  beforeDestroy() { // VUE_DEPRECATED_FEATURE alarm because 'beforeDestroy()' is deprecated.
    console.log("Destroying");
  }
});

rules.examplesRevised:
import { createApp, h } from 'vue';
import App from 'App.vue';
import Comp from 'Comp.vue';

const app = createApp({
  render() {
    return h(App);
  },
  beforeUnmount() {
    console.log("Unmounting");
  }
});
app.component('Comp', Comp);
app.mount('#app');

rules.examples:
import Vue from 'vue';

Vue.component('Hello', {
  propsData: { // VUE_MISPLACED_OPTION alarm because 'propsData' option is used in a Vue component option.
    msg: 'hello'
  },
  template: '<div>{{ msg }}</div>'
});

new Vue({
  el: '#app',
  template: '<Hello msg="hi" />'
});

rules.examplesRevised:
import Vue from 'vue';

Vue.component('Hello', {
  props: ['msg'],
  template: '<div>{{ msg }}</div>'
});

new Vue({
  el: '#app',
  template: '<Hello msg="hi" />'
});

rules.examples:
// Example 1
<template>
  <div>Hello</div> <!-- VUE_MULTIPLE_TEMPLATE_ROOT alarm because the template has two 'div' elements as a root. -->
  <div>Hi</div>
</template>

<script>
export default {
}
</script>

// Example 2
<template>
  <div v-for="item in list" :key="item.id">{{ item.name }}</div> <!-- VUE_MULTIPLE_TEMPLATE_ROOT alarm because the template has single root element with 'v-for' directive. -->
</template>

<script>
export default {
  data() {
    return { list: [{ id: 'hello', name: 'Hello' }, { id: 'hi', name: 'Hi' }] };
  }
}
</script>

rules.examplesRevised:
// Example 1
<template>
  <div v-if="isHello">Hello</div>
  <div v-else>Hi</div>
</template>

<script>
export default {
  data() {
    return { isHello: true };
  }
}
</script>

// Example 2
<template>
  <div>
    <div v-for="item in list" :key="item.id">{{ item.name }}</div>
  </div>
</template>

<script>
export default {
  data() {
    return { list: [{ id: 'hello', name: 'Hello' }, { id: 'hi', name: 'Hi' }] };
  }
}
</script>
